import React from 'react';
import styled from "styled-components";

const StyledTile = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
  background-color: rgb(224, 209, 241);

  &:hover {
    background-color: rgb(201, 174, 234);
  }
`
const WeatherText = styled.p`
  color: rgb(116, 29, 223)
`

const WeatherTile = ({ data }) => {
  let temp = `${data.main.temp}°C`
  let timestamp = data["dt_txt"].slice(10, -3)
  let weather = data.weather[0].description
  let icon = data.weather[0].icon
  let iconSrc = `http://openweathermap.org/img/w/${icon}.png`
  console.log(data)
  return (
      <StyledTile>
        <p>{timestamp}</p>
        <WeatherText>
          {temp}
        </WeatherText>
        <img src={iconSrc} alt="weather image"/>
        <WeatherText>
          {weather}
        </WeatherText>
      </StyledTile>    
    )
}

export default WeatherTile;
