import React, { useContext, useEffect, useState } from "react";
import styled from "styled-components";
import IndividualDay from "./IndividualDay";
import { WeatherContext } from "../../context/WeatherContext";
import WeatherTitle from "./WeatherTitle";
import { createArrayOfDates } from "../../utils";


let StyledContainer = styled.div`
  display: flex;
  width: 80%;
  margin-left: 10%;
  justify-content: center;
  @media (max-width: 575px) {
    flex-direction: column;
    width: 100%;
  }
`

const WeatherDisplay = () => {
  const [weather] = useContext(WeatherContext);
  const [city, setCity] = useState(null)
  const [forecast, setForecast] = useState(null)

  let dates = createArrayOfDates(new Date())

  const iterate = (array) => {
    for (let i=0; i < array.length(); i++) {
      console.log(array[i])
    }
  }
  useEffect(() => {
    if (weather === null) {
      return
    }
    else if (weather.cod !== "200") {
      return
    }
    else {
      setCity(weather.city.name)
      setForecast(weather.list)
      iterate(weather.list)
    }
  }, [weather])

  // DO YOU WANT TO DISPLAY THE CURRENT WEATHER IN THE UTC TIME (WHAT THE API GIVES YOU) OR DISPLAY THE CURRENT WEATHER IN LOCAL TIME 
  // this separates these days based off utc time (which is what the api goes by)
  // to separate each day, you have to go the weather object
  // every time the days position in the string of dt_txt changes the days changed and 

  return (
    <>
    <WeatherTitle city={city}/>
    <StyledContainer>
        {forecast === null ?
        null
        :
        dates.map((day, index) => 
          <IndividualDay key={day} date={day} forecast={forecast[index * 8]}/>
        )}

    </StyledContainer>
    </>
  )
}

export default WeatherDisplay;
